def proc(String input){
    def up = input.toCharArray().findAll { it == "(" }.size();
    def down = input.toCharArray().findAll { it == ")" }.size() * -1;
    return up + down;
}

def proc2(String input){
    char[] arr = input.toCharArray();
    
    int pos = 0;
    for(int i = 0;i < arr.length; i++){
        if(arr[i] == '('){
            pos++;
        } else if(arr[i] == ')'){
            pos--;
        }
        if(pos<0){
            return i+1;
        }
    }
}

